//--------------------------------------------------
function cargarPaises(){
		
		for(i=0;i<ArrayPaisesLista.length; i++){
				
				if(cuantosAutores==1){
					agregarItem(document.form1.elements["pais[]"], ArrayPaisesLista[i]);
				}else{
					agregarItem(document.form1.elements["pais[]"][cuantosAutores-1], ArrayPaisesLista[i]);
				}
	
		}
	
}
//--------------------------------------------------
function agregarItem(cual,txt){
	
	var oOption = document.createElement("OPTION");
	oOption.text = txt;
	oOption.value = txt;
	cual.options.add(oOption);

}
