function seleccionarCampos(cuantosAutores){
	//Le resto uno para trabajr tranqui con los arrays
	cuantosAutoresNum = cuantosAutores - 1

	if(cuantosAutoresNum>0){

		for(i=0;i<=cuantosAutoresNum; i++){
			
			document.form1.elements['id[]'][i].value = ArrayID[i];
			document.form1.elements['nombre[]'][i].value = ArrayNombres[i];
			document.form1.elements['apellido[]'][i].value = ArrayApellidos[i];
			document.form1.elements['institucion[]'][i].value = ArrayInstituciones[i];
			document.form1.elements['mail[]'][i].value = ArrayMails[i];
			//
		
			for (u=0;u<ArrayPaisesLista.length; u++){
	
				if(ArrayPaises[i] == document.form1.elements['pais[]'][i][u].value){
					
					document.form1.elements['pais[]'][i][u].selected = true;
					
				}
				
			}	
			
			//
			document.form1.elements['ciudad[]'][i].value = ArrayCiudades[i];
			document.form1.elements['agradecimientos[]'][i].value = ArrayAgradecimientos[i];
			//
		
			if(ArrayLees == (i+1)){
				document.form1.elements['lee'][i].checked = true;
			}
			//
			if(ArrayInscriptos[i] == 1){
				document.form1.elements['inscripto_[]'][i].checked = true;
			}else{
				document.form1.elements['inscripto_[]'][i].checked = false;
			}
			//
			
		}
	
	
	}else{
		//
			document.form1.elements['id[]'].value = ArrayID[0];
			document.form1.elements['nombre[]'].value = ArrayNombres[0];
			document.form1.elements['apellido[]'].value = ArrayApellidos[0];
			document.form1.elements['institucion[]'].value = ArrayInstituciones[0];
			document.form1.elements['mail[]'].value = ArrayMails[0];
			//
			
			for(u=0;u<ArrayPaisesLista.length; u++){
	
				if(ArrayPaises[0] == document.form1.elements['pais[]'][u].value){
					
					document.form1.elements['pais[]'][u].selected = true;
					
				}
				
			}
			
			//
			document.form1.elements['ciudad[]'].value = ArrayCiudades[0];
			document.form1.elements['agradecimientos[]'].value = ArrayAagradecimientos[0];
			//
	
			document.form1.elements['lee'].checked = true;

			//
			if(ArrayInscriptos[0] == 1){
				document.form1.elements['inscripto_[]'].checked = true;
			}else{
				document.form1.elements['inscripto_[]'].checked = false;
			}
		
		}

}
