//Validar forma
function validarAutor(){

	ningunoLee = false
	
	if(cuantosAutores>1){
	
		for(i=0;i<cuantosAutores;i++) {
			
			if(document.form1.elements['nombre[]'][i].value==""){
				if(i==0){
					alert("Por favor, escriba un nombre para el Autor 1");
				}else{
					alert("Por favor, escriba un nombre para el Autor " + (i+1));
				}
				document.form1.elements['nombre[]'][i].focus();
				return; 
			}
			
			if(document.form1.elements['apellido[]'][i].value==""){
				if(i==0){
					alert("Por favor, escriba un apellido para el Autor 1");
				}else{
					alert("Por favor, escriba un apellido para el Autor " + (i+1));
				}
				document.form1.elements['apellido[]'][i].focus();
				return; 
			}
			
			if(document.form1.elements['institucion[]'][i].value==""){
				if(i==0){
					alert("Por favor, escriba una institución para el Autor 1");
				}else{
					alert("Por favor, escriba una institución para el Autor " + (i+1));
				}
				document.form1.elements['institucion[]'][i].focus();
				return; 
			}
			
			if(document.form1.elements['mail[]'][i].value==""){
				if(i==0){
					alert("Por favor, escriba un mail para el Autor 1");
					document.form1.elements['mail[]'][i].focus();
					return; 
				}else{
				//	alert("Por favor, escriba un E-mail para el autor " + (i+1));
				}
			
			}
			
			
			if (document.form1.elements['mail[]'][i].value.indexOf('@', 0) == -1 || document.form1.elements['mail[]'][i].value.indexOf('.', 0) == -1) {
					
				if(i==0){
					alert("Please, write a e-mail for the Author 1");
					document.form1.elements['mail[]'][i].focus();
					return; 
				}else{
					//alert("Por favor, escriba un E-mail correcto para el autor " + (i+1));
				}
					
				
			}
				
			if(document.form1.elements['pais[]'][i].value=="Seleccione un país"){
				if(i==0){
					alert("Por favor, escriba un país para el Autor 1");
			
			}else{
					alert("Por favor, escriba un país para el Autor " + (i+1));
				}
				document.form1.elements['pais[]'][i].focus();
				return; 
			}	
			
			if(document.form1.elements['ciudad[]'][i].value==""){
				if(i==0){
					alert("Por favor, escriba una ciudad para el autor 1");
				}else{
					alert("Por favor, escriba una ciudad para el autor " + (i+1));
				}
				document.form1.elements['ciudad[]'][i].focus();
				return; 
			}
			
			if(document.form1.elements['agradecimientos[]'][i].value==""){
				if(i==0){
					alert("Agradecimientos, se busca poner aquí el nombre de la organización, empresa, etc.,  que apoyó la investigación.");
				}else{
					alert("Agradecimientos, se busca poner aquí el nombre de la organización, empresa, etc.,  que apoyó la investigación.");
				}
				document.form1.elements['agradecimientos[]'][i].focus();
				return; 
			}
			
			
			if(document.form1.elements['lee'][i].checked == true){		
				ningunoLee = true
			}
	
		}
	}else{
			if(document.form1.elements['nombre[]'].value==""){
				alert("Por favor, escriba un nombre para el Autor 1");
				document.form1.elements['nombre[]'].focus();
				return; 
			}
			
			if(document.form1.elements['apellido[]'].value==""){
				alert("Por favor, escriba un apellido para el Autor 1");
				document.form1.elements['apellido[]'].focus();
				return; 
			}
			
			if(document.form1.elements['institucion[]'].value==""){
				alert("Por favor, escriba una institución para el Autor 1");
				document.form1.elements['institucion[]'].focus();
				return; 
			}
			
			if(document.form1.elements['mail[]'].value==""){		
				alert("Por favor, escriba un mail para el Autor 1");
				document.form1.elements['mail[]'].focus();
				return; 
			}
			
			if (document.form1.elements['mail[]'].value.indexOf('@', 0) == -1 || document.form1.elements['mail[]'].value.indexOf('.', 0) == -1) {
				alert("Por favor, escriba un mail para el Autor 1");
				document.form1.elements['mail[]'].focus();
				return; 
			}
				
			if(document.form1.elements['pais[]'].value=="Seleccione un país"){
				alert("Por favor, escriba un pais para el Autor 1");
				document.form1.elements['pais[]'].focus();
				return; 
			}	
			if(document.form1.elements['ciudad[]'].value==""){
				alert("Por favor, escriba una ciudad para el Autor 1");
				document.form1.elements['ciudad[]'].focus();
				return; 
			}	
			if(document.form1.elements['agradecimientos[]'].value==""){
				alert("Agradecimientos, se busca poner aquí el nombre de la organización, empresa, etc.,  que apoyó la investigación.");
				document.form1.elements['agradecimientos[]'].focus();
				return; 
			}
			if(document.form1.elements['lee'].checked == true){		
				ningunoLee = true
			}
	
		
	}
	if(ningunoLee == false){
		alert("Un Autor debe ser presentador.");
		return; 
	}
	
	
		cargarLeeInscripto();
		
		form1.action="autoresGuardar.php";
		form1.target="_self" ;
		form1.submit();
	

	
}
